@@ -233,0 +233,19 @@
+    /**
+     * Set charset for System.out and System.err
+     */
+    private final static Charset nativeCharset;
+    static {
+        Charset cs;
+        Console cons;
+        if ((cons = System.console()) != null) {
+            cs = cons.charset();
+        } else {
+            try {
+                cs = Charset.forName(System.getProperty("native.encoding"));
+            } catch (Exception e) {
+                cs = Charset.defaultCharset();
+            }
+        }
+        nativeCharset = cs;
+    }
