-    height = rasterP->height;
-#ifdef WORKING
-    if (! JNU_IsNull(env, jbaseraster) &&
-        !(*env)->IsSameObject(env, rasterP->jraster, jbaseraster)) {
-        int xOff;
-        int yOff;
-        int baseWidth;
-        int baseHeight;
-        int baseXoff;
