-        baseYoff   = (*env)->GetIntField(env, jbaseraster, g_RasterYOffsetID);
-
-        if (xOff + rasterP->width < baseXoff + baseWidth) {
-            /* Can use edge */
-            width++;
-        }
-        if (yOff + rasterP->height < baseYoff + baseHeight) {
-            /* Can use edge */
-            height++;
-        }
-
-    }
