-                                                "<init>", "([B)V");
-            if (mid != NULL) {
-                result = (*env)->NewObject(env, strClazz, mid, bytes);
-            }
+            jstring utf8 = (*env)->NewStringUTF(env, "UTF-8");
+            result = (*env)->NewObject(env, strClazz,
+                                       String_init_ID, bytes, utf8);
+            (*env)->DeleteLocalRef(env, utf8);
