@@ -53,0 +53,6 @@
+inline void ZTracer::report_thread_event(const char* name, const Ticks& start, const Ticks& end) {
+  if (EventZThreadEvent::is_enabled()) {
+    send_thread_event(name, start, end);
+  }
+}
