+    public String getRawPropertyValue(String key) {
+        Map.Entry<String, String> entry = rawProperties.stream()
+                .filter(item -> item.getKey().equals(key))
+                .findFirst().orElse(null);
+
+        return entry == null ? null : entry.getValue();
+    }
