+        assertEquals(null, "foo", enquoter.applyTo("foo"));
+        assertEquals(null, "\" foo \"", enquoter.applyTo(" foo "));
+        assertEquals(null, "\"foo bar\"", enquoter.applyTo("foo bar"));
