+
+    @Test
+    @IR(counts = { IRNode.COUNTEDLOOP, "1" })
+    @IR(failOn = { IRNode.IF })
+    public static float test1() {
+        int i = 0;
+        int j;
+        float v = 1;
+        do {
+            v *= 2;
+            j = i;
+            i++;
+        } while (i < 10);
+        if (j < 10) {
+            v *= 2;
+        }
+        return v;
+    }
+
+    @Check(test = "test1")
+    public static void checkTest1(float res) {
+        if (res != Math.pow(2.0, 11)) {
+            throw new RuntimeException("incorrect result: " + res);
+        }
+    }
