@@ -40,0 +40,1 @@
+#include "memory/allocation.hpp"
@@ -3430,1 +3431,1 @@
-  jvmtiError err =JVMTI_ERROR_NOT_AVAILABLE;
+  NULL_CHECK(property, JVMTI_ERROR_NULL_POINTER);
@@ -3434,2 +3435,9 @@
-      if (p->set_writeable_value(value_ptr)) {
-        err =  JVMTI_ERROR_NONE;
+      switch (p->set_writeable_value(value_ptr, AllocFailStrategy::RETURN_NULL)) {
+      case SystemProperty::SUCCESS:
+        return JVMTI_ERROR_NONE;
+      case SystemProperty::OOM:
+        return JVMTI_ERROR_OUT_OF_MEMORY;
+      case SystemProperty::NOT_WRITEABLE:
+      default:
+        // We found a property, but it's not writeable
+        return JVMTI_ERROR_NOT_AVAILABLE;
@@ -3439,1 +3447,3 @@
-  return err;
+
+  // We cannot find a property of the given name
+  return JVMTI_ERROR_NOT_AVAILABLE;

@@ -128,3 +128,5 @@
-bool PathString::set_value(const char *value) {
-  if (_value != NULL) {
-    FreeHeap(_value);
+bool PathString::set_value(const char *value, AllocFailType alloc_failmode) {
+  char* new_value = AllocateHeap(strlen(value)+1, mtArguments, alloc_failmode);
+  if (new_value == NULL) {
+    assert(alloc_failmode == AllocFailStrategy::RETURN_NULL, "must be");
+    return false;
@@ -132,2 +134,0 @@
-  _value = AllocateHeap(strlen(value)+1, mtArguments);
-  assert(_value != NULL, "Unable to allocate space for new path value");
@@ -135,4 +135,1 @@
-    strcpy(_value, value);
-  } else {
-    // not able to allocate
-    return false;
+    FreeHeap(_value);
@@ -140,0 +137,2 @@
+  _value = new_value;
+  strcpy(_value, value);

@@ -31,0 +31,1 @@
+#include "memory/allocation.hpp"
@@ -69,1 +70,2 @@
-  bool set_value(const char *value);
+  // return false iff OOM && alloc_failmode == AllocFailStrategy::RETURN_NULL
+  bool set_value(const char *value, AllocFailType alloc_failmode = AllocFailStrategy::EXIT_OOM);
@@ -116,0 +118,6 @@
+  enum SetWriteableValueResult {
+    SUCCESS,
+    NOT_WRITEABLE,
+    OOM
+  };
+
@@ -123,3 +131,7 @@
-  bool set_writeable_value(const char *value) {
-    if (writeable()) {
-      return set_value(value);
+  SetWriteableValueResult set_writeable_value(const char *value,  AllocFailType alloc_failmode = AllocFailStrategy::EXIT_OOM) {
+    if (!writeable()) {
+      return NOT_WRITEABLE;
+    }
+    if (!set_value(value, alloc_failmode)) {
+      assert(alloc_failmode == AllocFailStrategy::RETURN_NULL, "must be");
+      return OOM;
@@ -127,1 +139,1 @@
-    return false;
+    return SUCCESS;
