@@ -384,0 +384,5 @@
+inline void Assembler::setbc(Register d, int biint)
+                                                  { emit_int32(SETBC_OPCODE | rt(d) | bi(biint)); }
+inline void Assembler::setnbc(Register d, int biint)
+                                                  { emit_int32(SETNBC_OPCODE | rt(d) | bi(biint)); }
+

@@ -11499,0 +11499,1 @@
+  predicate(!VM_Version::has_brw());
@@ -11507,0 +11508,31 @@
+instruct cmovI_conIvalueMinus1_conIvalue0_conIvalue1_bc(iRegIdst dst, flagsRegSrc crx) %{
+  effect(DEF dst, USE_DEF crx);
+  predicate(false);
+  ins_cost(DEFAULT_COST*3);
+
+  format %{ "SETBC   R0, GT\n\t"
+            "SETNBC  $dst, LT\n\t"
+            "OR      $dst, $dst, R0" %}
+
+  size(12);
+  ins_encode %{
+    __ setbc(R0, (($crx$$reg << 2) | 1) /* greater than */);
+    __ setnbc($dst$$Register, ($crx$$reg << 2) /* less than */);
+    __ or_unchecked($dst$$Register, $dst$$Register, R0);
+  %}
+
+  ins_pipe(pipe_class_default);
+%}
+
+instruct cmpL3_reg_reg_bc(iRegIdst dst, iRegLsrc src1, iRegLsrc src2) %{
+  match(Set dst (CmpL3 src1 src2));
+  ins_cost(DEFAULT_COST);
+  predicate(VM_Version::has_brw());
+
+  expand %{
+    flagsReg tmp1;
+    cmpL_reg_reg(tmp1, src1, src2);
+    cmovI_conIvalueMinus1_conIvalue0_conIvalue1_bc(dst, tmp1);
+  %}
+%}
+
