+    // calculate the least common multiple of two values
+    private static long lcm(long x, long y) {
+        if (x <= 0 || y <= 0)
+            throw new IllegalArgumentException("Non-positive parameter");
+
+        long u = x;
+        long v = y;
+
+        while (u != v) {
+            if (u < v)
+                u += x;
+            else // u > v
+                v += y;
+        }
+
+        return u;
+    }
