+// -----------------------------dump_name--------------------------------------
+void Node::dump_name(outputStream* st, DumpConfig* dc) const {
+  if (dc != nullptr) {
+    dc->pre_dump(st, this);
+  }
+  st->print("%s", Name());
+  if (dc != nullptr) {
+    dc->post_dump(st);
+  }
+}
