+import java.text.NumberFormat;
@@ -1126,0 +1127,3 @@
+        final double baseFileSize = 1000.0;
+        final MessageFormat mf = new MessageFormat("");
+        final NumberFormat nf = NumberFormat.getNumberInstance();
@@ -1131,0 +1135,1 @@
+            nf.setMinimumFractionDigits(1);
@@ -1182,0 +1187,1 @@
+            Object[] objs = new Object[1];
@@ -1192,2 +1198,10 @@
-            } else if (value instanceof Long) {
-                long len = ((Long) value) / 1024L;
+            } else if (value instanceof Long len) {
+                
+                /*
+                 * Code block is relevant to linux
+                 * File size display upto 1 decimal precision
+                 * Base-10 number system used for formatting file size
+                 * similar to linux file system
+                 * Empty file size show as 0.0 KB
+                 * 1->100 byte files show as 0.1 KB and so on
+                 */
@@ -1195,3 +1209,15 @@
-                    text = MessageFormat.format(kiloByteString, len + 1);
-                } else if (len < 1024L) {
-                    text = MessageFormat.format(kiloByteString, (len == 0L) ? 1L : len);
+                    updateMessageFormatPattern(kiloByteString);
+                    if (len == 0) {
+                        objs[0] = 0.0;
+                    } else if (len > 0 && len < 100L) {
+                        objs[0] = 0.1;
+                    } else {
+                        objs[0] = formatToDoubleValue(len);
+                    }
+                } else if (len < 100L) {
+                    updateMessageFormatPattern(kiloByteString);
+                    if (len == 0) {
+                        objs[0] = 0.0;
+                    } else {
+                        objs[0] = 0.1;
+                    }
@@ -1199,3 +1225,5 @@
-                    len /= 1024L;
-                    if (len < 1024L) {
-                        text = MessageFormat.format(megaByteString, len);
+                    double kbVal = formatToDoubleValue(len);
+                    len = (long)kbVal;
+                    if (kbVal < baseFileSize) {
+                        updateMessageFormatPattern(kiloByteString);
+                        objs[0] = kbVal;
@@ -1203,2 +1231,9 @@
-                        len /= 1024L;
-                        text = MessageFormat.format(gigaByteString, len);
+                        double mbVal = formatToDoubleValue(len);
+                        len = (long)mbVal;
+                        if (mbVal < baseFileSize) {
+                            updateMessageFormatPattern(megaByteString);
+                            objs[0] = mbVal;
+                        } else {
+                            updateMessageFormatPattern(gigaByteString);
+                            objs[0] = formatToDoubleValue(len);
+                        }
@@ -1207,0 +1242,1 @@
+                text = mf.format(objs);
@@ -1219,0 +1255,9 @@
+
+        private void updateMessageFormatPattern(String pattern) {
+            mf.applyPattern(pattern);
+            mf.setFormat(0, nf);
+        }
+
+        private static double formatToDoubleValue(long len) {
+            return (len / 100L) / 10.0d;
+        }

