@@ -7706,0 +7706,14 @@
+instruct incI_mem(memory dst, immI_1 src, rFlagsReg cr)
+%{
+  predicate(UseIncDec);
+  match(Set dst (StoreI dst (AddI (LoadI dst) src)));
+  effect(KILL cr);
+
+  ins_cost(125); // XXX
+  format %{ "incl    $dst\t# int" %}
+  ins_encode %{
+    __ incrementl($dst$$Address);
+  %}
+  ins_pipe(ialu_mem_imm);
+%}
+
