@@ -304,1 +304,1 @@
-            JLI_ReportErrorMessageSys("error locating main entrypoint\n");
+            JLI_ReportErrorMessageSys("error locating main entrypoint");
@@ -349,1 +349,1 @@
-        JLI_ReportErrorMessageSys("Could not create main thread: %s\n", strerror(errno));
+        JLI_ReportErrorMessageSys("Could not create main thread");
@@ -353,1 +353,1 @@
-        JLI_ReportErrorMessageSys("pthread_detach() failed: %s\n", strerror(errno));
+        JLI_ReportErrorMessageSys("pthread_detach() failed");

@@ -203,10 +203,0 @@
-    char *emsg;
-
-    /*
-     * TODO: its safer to use strerror_r but is not available on
-     * Solaris 8. Until then....
-     */
-    emsg = strerror(errno);
-    if (emsg != NULL) {
-        fprintf(stderr, "%s\n", emsg);
-    }
@@ -216,0 +206,14 @@
+
+    if (errno != 0) {
+        /*
+         * Buffer size of 1024 copied from typical POSIX size used
+         * in strerror_r
+         */
+        char error[1024];
+        if(strerror_r(errno, error, sizeof error) == 0) {
+            fprintf(stderr, ": %s", error);
+        } else {
+            fprintf(stderr, ": Java could not determine the underlying error");
+        }
+        errno = 0;
+    }

@@ -609,3 +609,3 @@
- * Just like JLI_ReportErrorMessage, except that it concatenates the system
- * error message if any, it's up to the calling routine to correctly
- * format the separation of the messages.
+ * Because Windows API errors and regular errno errors can exist at the same time,
+ * compromise and error out when both are present. Windows code should ideally clear
+ * the last error before a call that sets either of these!
@@ -617,2 +617,0 @@
-
-    int save_errno = errno;
@@ -623,0 +621,3 @@
+    char* const conflict = "Java detected conflicting Windows and C Runtime errors and is unable to provide an accurate report";
+    char* const unknown = "Java could not determine the underlying error";
+
@@ -625,13 +626,19 @@
-    if ((errval = GetLastError()) != 0) {               /* Platform SDK / DOS Error */
-        int n = FormatMessage(FORMAT_MESSAGE_FROM_SYSTEM|
-            FORMAT_MESSAGE_IGNORE_INSERTS|FORMAT_MESSAGE_ALLOCATE_BUFFER,
-            NULL, errval, 0, (LPTSTR)&errtext, 0, NULL);
-        if (errtext == NULL || n == 0) {                /* Paranoia check */
-            errtext = "";
-            n = 0;
-        } else {
-            freeit = JNI_TRUE;
-            if (n > 2) {                                /* Drop final CR, LF */
-                if (errtext[n - 1] == '\n') n--;
-                if (errtext[n - 1] == '\r') n--;
-                errtext[n] = '\0';
+    /* Check to catch conflicting errors */
+    if(GetLastError() != 0 && errno != 0) {
+        errtext = conflict;
+    } else {
+        if((errval = GetLastError()) != 0) {               /* Platform SDK / DOS Error */
+            int n = FormatMessage(FORMAT_MESSAGE_FROM_SYSTEM|
+                FORMAT_MESSAGE_IGNORE_INSERTS|FORMAT_MESSAGE_ALLOCATE_BUFFER,
+                NULL, errval, 0, (LPTSTR)&errtext, 0, NULL);
+            if (errtext == NULL || n == 0) {                /* Paranoia check */
+                errtext = unknown;
+                n = 0;
+            } else {
+                freeit = JNI_TRUE;
+                if (n > 3) {                                /* Drop final CR, LF */
+                    if (errtext[n - 1] == '\n') n--;
+                    if (errtext[n - 1] == '\r') n--;
+                    if (errtext[n - 1] == '.') n--;         /* Drop '.' to match getLastErrorString */
+                    errtext[n] = '\0';
+                }
@@ -640,2 +647,6 @@
-    } else {   /* C runtime error that has no corresponding DOS error code */
-        errtext = strerror(save_errno);
+
+        /* C runtime error that has no corresponding DOS error code */
+        if(errno != 0) {
+            errtext = strerror(errno);
+            if(errtext == NULL) errtext = unknown;
+        }
@@ -649,2 +660,2 @@
-        if (freeit) {
-           mlen += (int)JLI_StrLen(errtext);
+        if (errtext != NULL) {
+           mlen += 1 + (int)JLI_StrLen(errtext);
@@ -655,1 +666,0 @@
-        message[len]='\0';
@@ -657,2 +667,6 @@
-        if (freeit) {
-           JLI_StrCat(message, errtext);
+        if (errtext != NULL) {
+            message[len] = ':';
+            message[len + 1] = ' ';
+            JLI_StrCat(message, errtext);
+        } else {
+            message[len]='\0';
@@ -667,2 +681,2 @@
-        if (freeit) {
-           fprintf(stderr, "%s", errtext);
+        if (errtext != NULL) {
+           fprintf(stderr, ": %s", errtext);
@@ -670,0 +684,1 @@
+        fprintf(stderr, "\n");
