+    int errval;
@@ -71,18 +71,4 @@
-    if ((errval = GetLastError()) != 0) {
-        // DOS error
-        size_t n = (size_t)FormatMessage(
-                FORMAT_MESSAGE_FROM_SYSTEM|FORMAT_MESSAGE_IGNORE_INSERTS,
-                NULL,
-                errval,
-                0,
-                buf,
-                (DWORD)len,
-                NULL);
-        if (n > 3) {
-            // Drop final '.', CR, LF
-            if (buf[n - 1] == '\n') n--;
-            if (buf[n - 1] == '\r') n--;
