@@ -90,4 +76,3 @@
-
-    // C runtime error that has no corresponding DOS error code
-    if (errno == 0 || len < 1) return 0;
-    return strerror_s(buf, len, errno);
+    if (errval == 0 || len < 1) return 0;
+    getErrorString(errval, buf, len, origin);
+    return strlen(buf);
@@ -97,1 +82,1 @@
-getErrorString(int err, char *buf, size_t len)
+getErrorString(int err, char *buf, size_t len, ErrorOrigin origin)
@@ -99,4 +84,26 @@
-    int ret = 0;
-    if (err == 0 || len < 1) return 0;
-    ret = strerror_s(buf, len, err);
-    return ret;
+    if (origin == SYSTEM) {
+        DWORD errval = (DWORD) err;
+        if (errval != 0) {
+            // DOS error
+            size_t n = (size_t)FormatMessage(
+                    FORMAT_MESSAGE_FROM_SYSTEM|FORMAT_MESSAGE_IGNORE_INSERTS,
+                    NULL,
+                    errval,
+                    0,
+                    buf,
+                    (DWORD)len,
+                    NULL);
+            if (n > 3) {
+                // Drop final '.', CR, LF
+                if (buf[n - 1] == '\n') n--;
+                if (buf[n - 1] == '\r') n--;
+                if (buf[n - 1] == '.') n--;
+                buf[n] = '\0';
+            }
+            return n == 0 ? (int) GetLastError() : 0;
+        }
+    } else if (origin == RUNTIME) {
+        if (errno == 0 || len < 1) return 0;
+        return strerror_s(buf, len, errno);
+    }
+    return 0;

@@ -542,0 +542,17 @@
+void writeWindowMessage(const char* format, ...) {
+    va_list list;
+    va_start(list, format);
+    char *message;
+
+    /* get the length of the string we need */
+    int n = _vscprintf(format, list);
+
+    message = (char *) JLI_MemAlloc(n + 1);
+    _vsnprintf(message, n, format, list);
+    message[n]='\0';
+    MessageBox(NULL, message, "Java Virtual Machine Launcher",
+        (MB_OK|MB_ICONSTOP|MB_APPLMODAL));
+    JLI_MemFree(message);
+    va_end(list);
+}
+
@@ -608,69 +625,0 @@
-/*
- * Just like JLI_ReportErrorMessage, except that it concatenates the system
- * error message if any, it's up to the calling routine to correctly
- * format the separation of the messages.
- */
-JNIEXPORT void JNICALL
-JLI_ReportErrorMessageSys(const char *fmt, ...)
-{
-    va_list vl;
-
-    int save_errno = errno;
-    DWORD       errval;
-    jboolean freeit = JNI_FALSE;
-    char  *errtext = NULL;
-
-    va_start(vl, fmt);
-
-    if ((errval = GetLastError()) != 0) {               /* Platform SDK / DOS Error */
-        int n = FormatMessage(FORMAT_MESSAGE_FROM_SYSTEM|
-            FORMAT_MESSAGE_IGNORE_INSERTS|FORMAT_MESSAGE_ALLOCATE_BUFFER,
-            NULL, errval, 0, (LPTSTR)&errtext, 0, NULL);
-        if (errtext == NULL || n == 0) {                /* Paranoia check */
-            errtext = "";
-            n = 0;
-        } else {
-            freeit = JNI_TRUE;
-            if (n > 2) {                                /* Drop final CR, LF */
-                if (errtext[n - 1] == '\n') n--;
-                if (errtext[n - 1] == '\r') n--;
-                errtext[n] = '\0';
-            }
-        }
-    } else {   /* C runtime error that has no corresponding DOS error code */
-        errtext = strerror(save_errno);
-    }
-
-    if (IsJavaw()) {
-        char *message;
-        int mlen;
-        /* get the length of the string we need */
-        int len = mlen =  _vscprintf(fmt, vl) + 1;
-        if (freeit) {
-           mlen += (int)JLI_StrLen(errtext);
-        }
-
-        message = (char *)JLI_MemAlloc(mlen);
-        _vsnprintf(message, len, fmt, vl);
-        message[len]='\0';
-
-        if (freeit) {
-           JLI_StrCat(message, errtext);
-        }
-
-        MessageBox(NULL, message, "Java Virtual Machine Launcher",
-            (MB_OK|MB_ICONSTOP|MB_APPLMODAL));
-
-        JLI_MemFree(message);
-    } else {
-        vfprintf(stderr, fmt, vl);
-        if (freeit) {
-           fprintf(stderr, "%s", errtext);
-        }
-    }
-    if (freeit) {
-        (void)LocalFree((HLOCAL)errtext);
-    }
-    va_end(vl);
-}
-
