+    public int constantDiv(int x) {
+        return Integer.divideUnsigned(x, x);
+    }
+
+    @Test
+    @IR(failOn = {IRNode.UDIV_I})
+    // Checks x / 1 => x
+    public int identity(int x) {
+        return Integer.divideUnsigned(x, 1);
+    }
+
+    @Test
+    @IR(failOn = {IRNode.UDIV_I})
