+      this->max_ops = hb_clamp ((unsigned) (this->end - this->start) * HB_SANITIZE_MAX_OPS_FACTOR,
+                                (unsigned) HB_SANITIZE_MAX_OPS_MIN,
+                                (unsigned) HB_SANITIZE_MAX_OPS_MAX);
