+static bool is_phi_has_opaque1(Node* n) {
+  if (n->is_Phi()) {
+    for (uint i = 1; i < n->req(); i++) {
+      Node* in = n->in(i);
+      if (in != NULL && in->is_Opaque1()) {
+        return true;
+      }
+    }
+  }
+  return false;
+}
