+    private class AbstractSplittableGeneratorProxy extends AbstractSplittableGenerator {
+        @Override
+        public int nextInt() {
+            return SplittableRandom.this.nextInt();
+        }
+
+        @Override
+        public long nextLong() {
+            return SplittableRandom.this.nextLong();
+        }
+
