+             tmpL = isLU ? tmp1 : tmp2; // where to keep L for comparison
@@ -2377,3 +2363,11 @@
-    __ sub(tmp2, strL, cnt2); // strL pointer to load from
-    __ slli(t0, cnt2, 1);
