+    @SafeVarargs
+    public static <K, V> Map<K, V> join(Map<? extends K, V>... maps) {
+        Map<K, V> res = new HashMap<>();
+        for (Map<? extends K, V> e : maps) {
+            res.putAll(e);
+        }
+        return res;
+    }
+
+    @SafeVarargs
+    public static <V> List<V> joinValues(Map<?, V>... maps) {
+        List<V> res = new ArrayList<>();
+        for (Map<?, V> e : maps) {
+            res.addAll(e.values());
+        }
+        return res;
+    }
