+
+/* standard GSS method names (ordering is from mapfile) */
+static const char RELEASE_NAME[]                = "gss_release_name";
+static const char IMPORT_NAME[]                 = "gss_import_name";
+static const char COMPARE_NAME[]                = "gss_compare_name";
