+import java.util.HashMap;
+import java.util.Map;
+import java.util.NoSuchElementException;
+import static org.openjdk.jextract.clang.libclang.Index_h.*;
+
+public enum LinkageKind {
+    Invalid(CXLinkage_Invalid()),
+    NoLinkage(CXLinkage_NoLinkage()),
+    Internal(CXLinkage_Internal()),
+    UniqueExternal(CXLinkage_UniqueExternal()),
+    External(CXLinkage_External());
+
+    private final int value;
+
+    LinkageKind(int value) {
+        this.value = value;
+    }
+
+    public int value() {
+        return value;
+    }
+
+    private final static Map<Integer, LinkageKind> lookup;
+
+    static {
+        lookup = new HashMap<>();
+        for (LinkageKind e: LinkageKind.values()) {
+            lookup.put(e.value(), e);
+        }
+    }
+
+    public final static LinkageKind valueOf(int value) {
+        LinkageKind x = lookup.get(value);
+        if (null == x) {
+            throw new NoSuchElementException("Invalid Cursor kind value: " + value);
+        }
+        return x;
+    }
+}

