-    static void reducingChange(MultipleSelectionModelBase<?>.SelectedIndicesList selectedIndices, List<Integer> removed) {
-        if (removed.isEmpty()) return;
-
-        int startPos = 0;
-        int endPos = 1;
-        boolean firedOnce = false;
