++ (void)putString:(jobject)preferences key:(const char*)key value:(const char*)value {
+    GET_MAIN_JENV;
+
+    (*env)->CallObjectMethod(env, preferences, jMapPutMethod,
+        (*env)->NewStringUTF(env, key),
+        value != nil ? (*env)->NewStringUTF(env, value) : nil);
+}
