+
+public class ReferenceAsserts {
+
+    public static void testIfStronglyReferenced(Object obj, Runnable clearRefs) {
+        WeakReference<Object> ref = new WeakReference<>(obj);
+
+        clearRefs.run();
+        obj = null;
+
+        JMemoryBuddy.assertNotCollectable(ref);
+    }
