+public class Gtk2Deprecation1Test extends Gtk2DeprecationCommon {
+
+    @BeforeClass
+    public static void setup() throws Exception {
+        doSetup(true);
+    }
+
+    @AfterClass
+    public static void teardown() {
+        doTeardown();
