+/* Parsing functions for various MP4 standard extension atom groups */
+
+#ifdef HAVE_CONFIG_H
+#include "config.h"
+#endif
+
+#include <stdio.h>
+#ifdef GSTREAMER_LITE
+#include <string.h>
+#endif // GSTREAMER_LITE
+
+#include <gst/base/gstbytereader.h>
+#include <gst/tag/tag.h>
