+  {
+    ts.tv_sec = end_time / 1000000000;
+    ts.tv_nsec = end_time % 1000000000;
+
+    if ((status = pthread_cond_timedwait (cond, lock, &ts)) == 0)
+      return TRUE;
+  }
+#elif defined (HAVE_PTHREAD_COND_TIMEDWAIT_RELATIVE_NP)
