+    gint64 now = g_get_monotonic_time () * 1000;
+    gint64 relative;
+
+    if (end_time <= now)
+      return FALSE;
+
+    relative = end_time - now;
+
+    ts.tv_sec = relative / 1000000000;
+    ts.tv_nsec = relative % 1000000000;
