+import org.openjdk.jmc.flightrecorder.test.rules.jdk.TestEvent;
+
+public class EnvironmentVariableTestEvent extends TestEvent {
+	private final String key;
+
+	public EnvironmentVariableTestEvent(String key) {
+		super(JdkTypeIDs.ENVIRONMENT_VARIABLE);
+		this.key = key;
+	}
+
+	@SuppressWarnings("unchecked")
+	@Override
+	public <M> IMemberAccessor<M, IItem> getAccessor(IAccessorKey<M> attribute) {
+		if ("key".equals(attribute.getIdentifier())) {
+			return (IMemberAccessor<M, IItem>) MemberAccessorToolkit.<IItem, String, String> constant(key);
+		}
+		return null;
+	}
+
+}

