+class Event {
+	constructor({ type, attributes }) {
+		this.type = type;
+		if (!attributes) {
+			return;
+		}
+		this.time = attributes.startTime ||
+					attributes.endTime ||
+					attributes["(endTime)"]
+		this.eventThread = attributes.eventThread;
+		this.stackTrace = new StackTrace(attributes.stackTrace);
+		this.state = attributes.state;
+	}
+}
