+					if (Thread.currentThread().isAlive() && traceNode.isValid() && jsonModelBuilder.isValid()) {
+						DisplayToolkit.inDisplayThread()
+								.execute(() -> this.processModel(items, jsonModelBuilder.build()));
+					}
+				}
+			}
+			return null;
+		};
+
