+			DisplayToolkit.inDisplayThread().execute(() -> this.processModel(items, jsonModelBuilder.toString()));
+
+			return null;
+		};
+
+		return MODEL_EXECUTOR.schedule(modelPreparerTask, 100, TimeUnit.MICROSECONDS);
