+    @After
+    public void removeFiles() {
+        for (File f : usedFiles) {
+            if (f.exists()) {
+                if (f.isDirectory()) {
+                    deleteDirectory(f);
+                } else if (f.isFile()) {
+                    f.delete();
+                }
+                if (f.exists()) {
+                    System.err.println("Temporary file " + f.getAbsolutePath() + " was not removed. Please remove it manualy.");
+                }
+            }
+        }
+    }
