+   /**
+     * Adjust RC of ForkJoinPool.
+     *
+     * @param add determine increase or decrease of RC of ForkJoinPool
+     */
+    private void updateRC(boolean add) {
+        ForkJoinPool p;
+        Thread carrier = Thread.currentCarrierThread();
+        if (carrier instanceof ForkJoinWorkerThread &&
+            (p = ((ForkJoinWorkerThread)carrier).getPool()) != null) {
+            p.updateRC(add);
+         }
+    }
