+/*
+ * @test
+ * @compile --enable-preview -source ${jdk.version} VTCompensate.java
+ * @run testng/othervm --enable-preview -Djdk.defaultScheduler.parallelism=1 -Djdk.tracePinnedThreads=full VTCompensate
+ * @summary Basic test for compensate of ForkJoinPool.
+ */
+
+import java.util.concurrent.ExecutorService;
+import java.util.concurrent.Executors;
+import java.util.concurrent.ThreadFactory;
+import java.util.concurrent.locks.ReentrantLock;
+import static org.testng.Assert.*;
+import org.testng.annotations.Test;
+
+public class VTCompensate {
+    @Test
+    public static void pinCompensate() throws Exception {
+        ReentrantLock lock = new ReentrantLock();
+        Runnable vt0_r = new Runnable() {
+            @Override
+            public void run() {
+                System.out.println("enter vt0");
+                lock.lock();
+                lock.unlock();
+            }
+        };
+
+        Runnable vt1_r = new Runnable() {
+            @Override
+            public void run() {
+                synchronized (this) {
+                    lock.lock();
+                }
+                lock.unlock();
+            }
+        };
+
+        lock.lock();
+
+        Thread vt0 = Thread.ofVirtual().name("vt0").start(vt0_r);
+        Thread.sleep(1000);
+
+        Thread vt1 = Thread.ofVirtual().name("vt1").start(vt1_r);
+        Thread.sleep(5000);
+
+        lock.unlock();
+        vt0.join();
+        vt1.join();
+    }
+}
