+    ResourceMark rm(THREAD);
+    Symbol* name = module_entry->name();
+    log_debug(module)("add_module_enable_native_access(): module"
+                      " %s is marked as a native module",
+                       name == NULL ? UNNAMED_MODULE : name->as_C_string());
+  }
