+           }
+       }
+    }
+
+    private static void assertArrayEquals(long[] r, long[] a, long[] b, long[] c, boolean[] m, LTenOp f) {
+       for (int i = 0; i < LENGTH; i++) {
+           if (m[i % L_SPECIES.length()]) {
+               Asserts.assertEquals(f.apply(a[i], b[i], c[i]), r[i]);
+           } else {
