+const bool Matcher::match_rule_supported_masked_vector(int opcode, int vlen, BasicType bt) {
+  if (!match_rule_supported(opcode) || !vector_size_supported(bt, vlen)) {
+    return false;
+  }
+
+  if (UseSVE > 0) {
+    return masked_op_sve_supported(opcode);
+  }
+  // Only SVE support masked operations.
+  return false;
+}
