+instruct storeV_mask(vReg src, vmemA mem, pRegGov pg) %{
+  predicate(UseSVE > 0 && n->as_StoreVector()->memory_size() >= 16 &&
+            n->as_StoreVector()->memory_size() == MaxVectorSize);
+  match(Set mem (StoreVectorMasked mem (Binary src pg)));
+  ins_cost(SVE_COST);
+  format %{ "sve_str $mem, $pg, $src\t # vector (sve)" %}
+  ins_encode %{
+    FloatRegister src_reg = as_FloatRegister($src$$reg);
+    BasicType bt = vector_element_basic_type(this, $src);
+    loadStoreA_predicate(C2_MacroAssembler(&cbuf), true, src_reg, as_PRegister($pg$$reg),
+                         bt, elemType_to_regVariant(bt), $mem->opcode(),
+                         as_Register($mem$$base), $mem$$index, $mem$$scale, $mem$$disp);
+  %}
+  ins_pipe(pipe_slow);
+%}
+
+// vector to mask
+
+instruct vector2vmask(pRegGov pg, vReg src, rFlagsReg cr) %{
+  predicate(UseSVE > 0);
+  match(Set pg (VectorToMask src));
+  effect(KILL cr, DEF pg, USE src);
+  ins_cost(SVE_COST);
+  format %{ "sve_cmpeq  $pg, $src, -1\t# vector mask (sve)" %}
+  ins_encode %{
+    BasicType bt = vector_element_basic_type(this, $src);
+    __ sve_cmpeq(as_PRegister($pg$$reg), elemType_to_regVariant(bt),
+                 ptrue, as_FloatRegister($src$$reg), -1);
+  %}
+  ins_pipe(pipe_slow);
+%}
+
+// vector compare mask
+
+instruct vcmpmask_integral(pRegGov pg, vReg src1, vReg src2, immI cond, rFlagsReg cr) %{
+  predicate(UseSVE > 0 &&
+            is_integral_type(n->in(1)->in(1)->bottom_type()->is_vect()->element_basic_type()));
+  match(Set pg (VectorCmpMaskGen (Binary src1 src2) cond));
+  effect(KILL cr, DEF pg);
+  ins_cost(SVE_COST);
+  format %{ "sve_cmp $pg, $src1, $src2\t# vector cmp (sve)" %}
+  ins_encode %{
+    int esize = vector_mask_element_size(this);
+    Assembler::SIMD_RegVariant variant = elemBytes_to_regVariant(esize);
+    sve_compare_integral(C2_MacroAssembler(&cbuf), as_PRegister($pg$$reg),
+                   variant, ptrue, as_FloatRegister($src1$$reg),
+                   as_FloatRegister($src2$$reg), (int)$cond$$constant);
+  %}
+  ins_pipe(pipe_slow);
+%}
+
+instruct vcmpmask_fp(pRegGov pg, vReg src1, vReg src2, immI cond, rFlagsReg cr) %{
+  predicate(UseSVE > 0 &&
+            is_floating_point_type(n->in(1)->in(1)->bottom_type()->is_vect()->element_basic_type()));
+  match(Set pg (VectorCmpMaskGen (Binary src1 src2) cond));
+  effect(KILL cr, DEF pg);
+  ins_cost(SVE_COST);
+  format %{ "sve_cmp $pg, $src1, $src2\t# vector cmp (sve)" %}
+  ins_encode %{
+    int esize = vector_mask_element_size(this);
+    Assembler::SIMD_RegVariant variant = elemBytes_to_regVariant(esize);
+    sve_compare_fp(C2_MacroAssembler(&cbuf), as_PRegister($pg$$reg),
+                   variant, ptrue, as_FloatRegister($src1$$reg),
+                   as_FloatRegister($src2$$reg), (int)$cond$$constant);
+  %}
+  ins_pipe(pipe_slow);
+%}
+
+// mask to vector
+
+instruct vmask2vector(vReg dst, pRegGov pg) %{
+  predicate(UseSVE > 0);
+  match(Set dst (MaskToVector pg));
+  ins_cost(SVE_COST);
+  format %{ "sve_cpy $dst, $pg, -1\t# mask to vector (sve)" %}
+  ins_encode %{
+    BasicType bt = vector_element_basic_type(this);
+    __ sve_cpy(as_FloatRegister($dst$$reg), elemType_to_regVariant(bt),
+               as_PRegister($pg$$reg), -1, false);
+  %}
+  ins_pipe(pipe_slow);
+%}
+
