@@ -1017,1 +1016,2 @@
-    log_info(gc, free)("Flipped region " SIZE_FORMAT " to old reserve, but did not transfer capacity.", idx);
+    log_warning(gc, free)("Forcing transfer of " SIZE_FORMAT " to old reserve.", idx);
