@@ -354,1 +354,1 @@
-        var orgsToSearch = orgs.stream().map(o -> "org:" + o).collect(Collectors.joining("+"));
+        var orgsToSearch = orgs.stream().map(o -> "org:" + o).collect(Collectors.joining(" "));
@@ -356,1 +356,1 @@
-            orgsToSearch = "+" + orgsToSearch;
