+                .body("labels", String.join(",", labels))
+                // Temporary workaround for GitLab returning 500 when changing labels.
+                // The labels are still modified.
+                .onError(response -> {
+                    if (response.statusCode() == 500) {
+                        return Optional.of(JSON.of());
+                    }
+                    return Optional.empty();
+                })
+                .execute();
