+    public static Optional<String> integratedBranch(final PullRequest pull) {
+        if (isIntegrationBranch(pull.targetRef())) {
+            final String integrationMarker = "integrated/" + pull.id() + "/";
+            final int branchStart = pull.targetRef().indexOf(integrationMarker);
+            if (branchStart == -1) {
+                throw new IllegalStateException("Cannot parse integration branch " + pull.targetRef());
+            }
+            return Optional.of(pull.targetRef().substring(branchStart + integrationMarker.length()));
+        } else {
+            return Optional.empty();
+        }
+    }
+
+    public static boolean isIntegrationBranch(final String name) {
+        return name.startsWith("integrated/");
+    }
+
+    public static String integrationBranch(final PullRequest pull, final String branch) {
+        return "integrated/" + pull.id() + "/" + branch;
+    }
