+    @Override
+    public void merge(MergeMethod method) {
+        if (method == MergeMethod.REBASE) {
+            request.put("rebase")
+                   .execute();
+        } else {
+            request.put("merge")
+                   .body("squash", method == MergeMethod.SQUASH ? "true" : "false")
+                   .execute();
+        }
+    }
