+    static void markIntegratedAndMerged(PullRequestBot bot, PullRequest pr, Path scratchPath, Hash hash, PrintWriter reply, String subdir) {
@@ -331,0 +331,10 @@
+        final String branch = pr.targetRef();
+        final String integration = "integrated/" + pr.id();
+        try {
+            Repository repository = materializeLocalRepo(bot, pr, scratchPath, subdir);
+            Hash tip = repository.fetch(pr.repository().url(), branch, false);
+            repository.push(tip, pr.repository().url(), integration, true);
+        } catch (IOException exception) {
+            throw new UncheckedIOException(exception);
+        }
+        pr.setTargetRef(integration);
@@ -332,1 +342,1 @@
