@@ -343,1 +353,3 @@
-        reply.println(":bulb: You may see a message that your pull request was closed with unmerged commits. This can be safely ignored.");
+        reply.println(":bulb: You may see a message that your pull request was closed with unmerged commits, or that it was merged into a different branch. This can be safely ignored.");
+        pr.setTargetRef(branch);
+        pr.repository().deleteBranch(integration);

